<?php

require_once("config/database.php"); 

function execute_query($some_query, $database) {
    // prepare query
    $stmt = $database->prepare($some_query);

    if($stmt->execute()){
        return true;
    }else{
        echo "<pre>";
            print_r($stmt->errorInfo());
        echo "</pre>";
 
        return false;
    }
}
function create_user($data,$db){
	$query = "INSERT INTO 
                " . 'users' . "
            SET 
                username=:username, email=:email, level=:level, password=:password";
    
    // prepare query
    $stmt = $db->prepare($query);

    // posted values
    $username=htmlspecialchars(strip_tags($data['username']));
    $email=htmlspecialchars(strip_tags($data['email']));
    $level=htmlspecialchars(strip_tags($data['level']));
    $password=password_hash(htmlspecialchars(strip_tags($data['password'])), PASSWORD_DEFAULT);
 
    // bind values
    $stmt->bindParam(":username", $username);
    $stmt->bindParam(":email", $email);
    $stmt->bindParam(":level", $level);
    $stmt->bindParam(":password", $password);

    if($stmt->execute()){
        return true;
    }else{
        echo "<pre>";
            print_r($stmt->errorInfo());
        echo "</pre>";
        return false;
    }
}
function create_news($data,$db){
	$query = "INSERT INTO 
                " . 'news' . "
            SET 
                title_uk=:title_uk, title_ru=:title_ru, title_en=:title_en, full_text_uk=:full_text_uk, full_text_ru=:full_text_ru, full_text_en=:full_text_en, short_text_uk=:short_text_uk, short_text_ru=:short_text_ru, short_text_en=:short_text_en, images_nums=:images_nums";
    
    // prepare query
    $stmt = $db->prepare($query);

    // posted values
    $title_uk=htmlspecialchars(strip_tags($data['title_uk']));
    $title_ru=htmlspecialchars(strip_tags($data['title_ru']));
    $title_en=htmlspecialchars(strip_tags($data['title_en']));
    $full_text_uk=htmlspecialchars(strip_tags($data['full_text_uk']));
    $full_text_ru=htmlspecialchars(strip_tags($data['full_text_ru']));
    $full_text_en=htmlspecialchars(strip_tags($data['full_text_en']));
    $short_text_uk=htmlspecialchars(strip_tags($data['short_text_uk']));
    $short_text_ru=htmlspecialchars(strip_tags($data['short_text_ru']));
    $short_text_en=htmlspecialchars(strip_tags($data['short_text_en']));
    $images_nums=htmlspecialchars(strip_tags($data['images_nums']));
 
    // bind values
    $stmt->bindParam(":title_uk", $title_uk);
    $stmt->bindParam(":title_ru", $title_ru);
    $stmt->bindParam(":title_en", $title_en);
    $stmt->bindParam(":full_text_uk", $full_text_uk);
    $stmt->bindParam(":full_text_ru", $full_text_ru);
    $stmt->bindParam(":full_text_en", $full_text_en);
    $stmt->bindParam(":short_text_uk", $short_text_uk);
    $stmt->bindParam(":short_text_ru", $short_text_ru);
    $stmt->bindParam(":short_text_en", $short_text_en);
    $stmt->bindParam(":images_nums", $images_nums);

    if($stmt->execute()){
        return true;
    }else{
        echo "<pre>";
            print_r($stmt->errorInfo());
        echo "</pre>";
        return false;
    }
}

